`,(0,o.jsxDEV)(t.pre,{children:(0,o.jsxDEV)(t.code,{className:"language-js:debounce.js",children:`const debounce = (callback, wait) => {
  let timeoutId = null;

  return (...args) => {
    clearTimeout(timeoutId);

    timeoutId = setTimeout(() => {
      callback(...args);
    }, wait);
  };
};
